import{useState,useEffect}from"react";import{db}from"@/firebase";import{collection,getDocs}from"firebase/firestore";const useJobFeatures=()=>{const[jobFeatures,setJobFeatures]=useState([]),[loading,setLoading]=useState(!0);return useEffect((()=>{(async()=>{try{const querySnapshot=await getDocs(collection(db,"jobs")),jobsArray=[];querySnapshot.forEach((doc=>{jobsArray.push({id:doc.id,...doc.data()})})),setJobFeatures(jobsArray)}catch(error){console.error("Error fetching job features: ",error.message),"permission-denied"===error.code&&console.error("Permission denied. Check Firestore rules.")}finally{setLoading(!1)}})()}),[]),{jobFeatures:jobFeatures,loading:loading}};export default useJobFeatures;