import{db}from"@/firebase";import{collection,getDocs}from"firebase/firestore";export const fetchUsers=async()=>{try{const usersCollection=collection(db,"users"),userSnapshot=await getDocs(usersCollection);return userSnapshot.docs.map((doc=>({id:doc.id,...doc.data()})))}catch(error){return console.error("Error fetching users:",error),[]}};